import React, { useState } from 'react';
import { allRecipes } from '../data/allRecipes.js';
import './IngredientSearch.css';

const IngredientSearch = ({ onSearch }) => {
  const [ingredients, setIngredients] = useState('');
  const [searchTerm, setSearchTerm] = useState('');

  const handleIngredientChange = (e) => {
    setIngredients(e.target.value);
  };

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const searchRecipes = (ingredients, searchTerm) => {
    let results = [...allRecipes];
    
    // Only search by ingredients if there are 2 or more
    if (ingredients && ingredients.length >= 2) {
      results = results.filter(recipe => 
        ingredients.every(ingredient => 
          recipe.ingredients.some(recipeIngredient => 
            recipeIngredient.toLowerCase().includes(ingredient.toLowerCase())
          )
        )
      );
    } else if (ingredients && ingredients.length === 1) {
      // If only one ingredient, don't show any recipes
      results = [];
    }
    
    // Search by recipe name or tags (if searchTerm is provided)
    if (searchTerm) {
      results = results.filter(recipe => 
        recipe.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        recipe.tags.some(tag => tag.toLowerCase().includes(searchTerm.toLowerCase()))
      );
    }
    
    return results;
  };

  const handleSearch = () => {
    const ingredientList = ingredients.split(',').map(ing => ing.trim()).filter(ing => ing);
    const searchResults = searchRecipes(ingredientList, searchTerm.trim());
    
    if (onSearch) {
      onSearch(searchResults);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSearch();
    }
  };

  return (
    <div className="ingredient-search">
      <div className="search-container">
        <div className="search-section">
          <label htmlFor="ingredients">Ingredients (comma-separated):</label>
          <input
            type="text"
            id="ingredients"
            value={ingredients}
            onChange={handleIngredientChange}
            onKeyPress={handleKeyPress}
            placeholder="e.g., chicken, rice, tomatoes"
            className="ingredient-input"
          />
        </div>
        
        <div className="search-section">
          <label htmlFor="search">Search recipes:</label>
          <input
            type="text"
            id="search"
            value={searchTerm}
            onChange={handleSearchChange}
            onKeyPress={handleKeyPress}
            placeholder="Search for recipes..."
            className="search-input"
          />
        </div>
        
        <button 
          onClick={handleSearch}
          className="search-button"
          disabled={(!ingredients && !searchTerm) || (ingredients.split(',').filter(ing => ing.trim()).length === 1)}
        >
          Search Recipes
        </button>
      </div>
    </div>
  );
};

export default IngredientSearch;
